<template>
    <li class="todo">
        <div class="todo__wrapper">
            <button class="todo__btn todo__btn--edit"
                    @click.prevent="editToDo(id)"></button>
            <button class="todo__btn todo__btn--delete"
                    @click.prevent="delToDo(id)"></button>
        </div>
        <div class="todo__inner">
            <h3 class="todo__title">{{title}}</h3>
            <p class="todo__description">{{description}}</p>
        </div>
        <CreateToDo @click.prevent="closeModal" v-if="modalShow"
                    v-bind:id="id"
                    v-bind:title="title"
                    v-bind:description="description"
                    v-bind:action="action"
                    @clicked="closeModal"/>
    </li>
</template>

<script>
    import {mapActions, mapState} from 'vuex';
    import CreateToDo from './ModalToDo';

    export default {
        name: "TodoItem",
        components: {
            CreateToDo
        },
        data() {
            return {
                modalShow: false,
                action: ''
            }
        },
        props: {
            id: String,
            title: String,
            description: String
        },
        computed: {
            ...mapState({
                authUser: state => state.UserStore.authUser
            })
        },
        methods: {
            closeModal() {
                this.modalShow = false;
            },
            editToDo(id) {
                this.action = 'PATCH';
                this.modalShow = true;
            },
            delToDo(id) {
                const payload = {
                    id,
                    authUser: this.authUser
                }
                this.$store.dispatch('ActionDel', payload)
                this.modalShow = false;
            }
        }
    }
</script>

