    .todo {
        border: 1px solid #333333;

        &__wrapper {
            display: flex;
            align-items: center;
            justify-content: flex-end;
            border-bottom: 1px solid #333333;
            padding: 5px;
        }

        &__inner {
            padding: 5px;
        }

        &__title, &__description {
            text-align: left;
            word-wrap: break-word;
            overflow-wrap: break-word;
        }

        &__btn {
            border: none;
